mapper.insert:
/** logging-mysql-mapper|saveApiLogging|julim  */
INSERT INTO tb_cmm_api_log (
    request_id
    , system_id
    , req_system_id
    , method
    , uri
    , param
    , ip
    , access_token
    , session_id
    , success
    , response
    , message
    , regist_dt
    , regist_id
) VALUES (
  #{requestId}
  , #{systemId}
  , #{reqSystemId}
  , #{method}
  , #{uri}
  , #{param}
  , #{ip}
  , #{accessToken}
  , #{sessionId}
  , #{success}
  , #{response}
  , #{message}
  , now(3)
  , #{registId}
)

mapper.update:
/** logging-mysql-mapper|updateLogging|julim  */
UPDATE tb_cmm_api_log
   SET success = #{success}
     , response = #{response}
     , message = #{message}
     , updt_dt = now(3)
     , updt_id = #{updtId}
 WHERE request_id = #{requestId}

mapper.select:
/** logging-mysql-mapper|selectApiLogging|julim */ SELECT request_id , system_id , req_system_id , method , uri , param , ip , access_token , session_id , regist_dt , regist_id FROM tb_cmm_api_log
AND request_id = #{requestId}
AND system_id = #{systemId}
AND uri LIKE CONCAT('%', #{url}, '%')
